x-codeSamples.source:
import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.applePayDomain.register({
  domainName: 'example.com'
});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.bizum.validatePhone({
  accountId: '6dccce85-b290-40c2-bb9c-57bc7ecfaf04',
  phoneNumber: '+34600000000'
});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.paymentMethods.get({accountId: '6dccce85-b290-40c2-bb9c-57bc7ecfaf04'});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.payments.create({
  amount: 110, // 1.10 EUR
  currency: 'EUR',
  orderId: '000001',
  callbackUrl: 'https://example.com/checkout/callback',
  completeUrl: 'https://example.com/checkout/complete'
});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.payments.get('832b77d1a4b372349a7ae0bb1b2af059');

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.payments.confirm('832b77d1a4b372349a7ae0bb1b2af059', {
  paymentToken: '7cc38b08ff471ccd313ad62b23b9f362b107560b'
});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

// Capture a payment providing the payment id.
// Optionally you can specify the amount to capture.
// If amount is not specified, the total amount of the payment will be captured.
monei.payments.capture('832b77d1a4b372349a7ae0bb1b2af059', {
  amount: 100 // 1.00 EUR
});

import {Monei, PaymentCancellationReason} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.payments.cancel('832b77d1a4b372349a7ae0bb1b2af059', {
  cancellationReason: PaymentCancellationReason.REQUESTED_BY_CUSTOMER
});

import {Monei, PaymentRefundReason} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.payments.refund('832b77d1a4b372349a7ae0bb1b2af059', {
  refundReason: PaymentRefundReason.REQUESTED_BY_CUSTOMER
});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.payments.recurring('7cc38b08ff471ccd313ad62b23b9f362b107560b', {
  orderId: '000001',
  callbackUrl: 'https://example.com/checkout/callback'
});

import {Monei, PaymentMessageLanguage} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.payments.sendRequest('832b77d1a4b372349a7ae0bb1b2af059', {
  phoneNumber: '+34500000000',
  language: PaymentMessageLanguage.ES
});

import {Monei, PaymentMessageLanguage} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.payments.sendLink('832b77d1a4b372349a7ae0bb1b2af059', {
  customerEmail: 'john.doe@example.com',
  language: PaymentMessageLanguage.ES
});

import {Monei, PaymentMessageLanguage} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.payments.sendReceipt('832b77d1a4b372349a7ae0bb1b2af059', {
  customerEmail: 'john.doe@example.com',
  language: PaymentMessageLanguage.ES
});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.subscriptions.create({
  customer: {
    name: 'John Doe',
    email: 'john.doe@monei.com'
  },
  amount: 110,
  currency: 'EUR',
  interval: 'month',
  intervalCount: 1,
  description: 'MoonMail Lite Monthly',
  callbackUrl: 'https://example.com/subscription/callback',
  paymentCallbackUrl: 'https://example.com/payment/callback'
});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.subscriptions.get('575bcd84-09fc-4a6e-8c4c-f88b8eb90bfa');

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.subscriptions.update('575bcd84-09fc-4a6e-8c4c-f88b8eb90bfa', {amount: 990});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.subscriptions.activate('575bcd84-09fc-4a6e-8c4c-f88b8eb90bfa', {
  paymentToken: '7cc38b08ff471ccd313ad62b23b9f362b107560b'
});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.subscriptions.cancel('575bcd84-09fc-4a6e-8c4c-f88b8eb90bfa', {cancelAtPeriodEnd: true});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.subscriptions.pause('575bcd84-09fc-4a6e-8c4c-f88b8eb90bfa', {pauseAtPeriodEnd: true});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.subscriptions.resume('575bcd84-09fc-4a6e-8c4c-f88b8eb90bfa');

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.subscriptions.sendLink('sub_7cc38b08ff471ccd313ad62b23b9f362', {
  email: 'customer@example.com',
  language: 'en'
});

import {Monei} from '@monei-js/node-sdk';

const monei = new Monei('pk_test_36cf3e8a15eff3f5be983562ea6b13ec');

monei.subscriptions.sendStatus('sub_7cc38b08ff471ccd313ad62b23b9f362', {
  email: 'customer@example.com',
  language: 'en'
});
